def try_convert_number(value: str) -> int | float | str:
    try:
        if '.' in value:
            return float(value)
        return int(value)
    except ValueError:
        return value

def parse_key_values(text: str) -> dict[str, str]:
    result = {}
    for line in text.strip().split('\n'):
        if '=' in line:
            key, value = line.split('=', 1)
            result[key.strip()] = value.strip()
    return result
