export function hasRequiredProps(obj: Record<string, any>, required: string[]): boolean {
  return required.every(prop => prop in obj);
}

export function checkType(value: any, expectedType: string): boolean {
  if (expectedType === 'array') {
    return Array.isArray(value);
  }
  if (expectedType === 'null') {
    return value === null;
  }
  return typeof value === expectedType;
}

export function validateEmail(email: string): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  if (!email || email.length === 0) {
    errors.push('Email is required');
  } else {
    if (!email.includes('@')) {
      errors.push('Email must contain @');
    }
    if (!email.includes('.')) {
      errors.push('Email must contain a domain');
    }
    if (email.startsWith('@') || email.endsWith('@')) {
      errors.push('Email has invalid @ position');
    }
  }

  return { valid: errors.length === 0, errors };
}
