export function parseQueryString(query: string): Record<string, string> {
  const params: Record<string, string> = {};

  if (!query || query.length === 0) {
    return params;
  }

  const pairs = query.split('&');
  for (const pair of pairs) {
    const [key, value] = pair.split('=');
    if (key) {
      params[decodeURIComponent(key)] = decodeURIComponent(value || '');
    }
  }

  return params;
}

export function splitString(str: string, delimiter: string): string[] {
  const result: string[] = [];
  let current = '';

  for (const char of str) {
    if (char === delimiter) {
      result.push(current);
      current = '';
    } else {
      current += char;
    }
  }

  if (current.length > 0) {
    result.push(current);
  }

  return result;
}

export function parseEmail(email: string): { local: string; domain: string; tld: string } | null {
  if (!email || !email.includes('@')) {
    return null;
  }

  const atIndex = email.lastIndexOf('@');
  const local = email.substring(0, atIndex);
  const domainPart = email.substring(atIndex + 1);

  const dotIndex = domainPart.lastIndexOf('.');
  if (dotIndex === -1) {
    return null;
  }

  const domain = domainPart.substring(0, dotIndex);
  const tld = domainPart.substring(dotIndex + 1);

  return { local, domain, tld };
}
